import { useState, useEffect, useContext } from 'react';
import { useTranslation } from 'react-i18next';
import { makeStyles } from '@mui/styles';
import { FixedSizeList } from 'react-window';
import AnalysisContext from '../../Analysis-context/AnalysisContext';
import Map from '../mapLog/Map';
import Row from './data/Row';
import DialogRecordDetailLogAnalysis from '../../dialogs/DialogRecordDetailLogAnalysis';
import buildTable from '../../tools/dicoin_analysis';

const useStyles = makeStyles({
  heightCalc: {
    '& > div:nth-child(1)': {
      height: 'calc(100vh - 40rem)',
      '@media (max-width: 599px)': {
        height: 'auto',
      },
    },
    '& > div:nth-child(2)': {
      '& > div': {
        height: '331px',
      },
    },
  },
  heightCalcTable: {
    '& > div': {
      '&:nth-child(2)': {
        borderTop: '1px solid silver',
      },
      '@media (max-width: 599px)': {
        height: '100%',
      },
    },
  },
  ERROR: {
    color: '#f44336',
  },
  WARNING: {
    color: 'orange',
  },
  OK: {
    color: '#67BB7A',
  },

  /** ************** */
  tableExecution: {
    '& li': {
      justifyContent: 'space-between',
      paddingTop: '8px',
      paddingBottom: '8px',
      paddingLeft: '0px',
      paddingRight: '0px',
      display: 'grid',
      'grid-template-columns': '9% 11.5% 21% 10.2% 14.6% 11.1% 11.1% 11.5%',
      cursor: 'pointer',
      '&:hover': {
        background: 'silver',
      },
    },
  },
  tableIcon: {
    position: 'relative',
    '& span': {
      position: 'absolute',
      left: '0px',
      fontSize: '24px',
      top: '-1px',
    },
    '& p': {
      marginTop: '16px',
      marginLeft: '21px',
      fontSize: '1rem',
    },
  },
  check: {
    color: '#66bb6a',
  },
  error: {
    color: '#f44336',
  },
  warning: {
    color: '#ff9a00',
  },
});

const recordStates = {
  PERSISTED: 'save',
  SENT: 'check',
  ERROR: 'error',
  WARNING: 'warning',
  NOTE: 'note',
};

export default function LogAnalysis(props) {
  const {
    appFileContent,
    authorityVehicleFileContent,
    isOwnReady,
    mapChangedTimestamp,
    setMapChangedTimestamp,
    isauthorityCompanyReady,
    availableHeight,
    rowHeight,
  } = useContext(AnalysisContext);
  const { t } = useTranslation('analysisPage');
  const classes = useStyles();

  // #region dialog statistics
  const [openDialogRecordDetailLogAnalysis, setOpenDialogRecordDetailLogAnalysis] = useState(false);
  const [dataDialogDetails, setDataDialogDetails] = useState();
  const [indexDetails, setIndexDetails] = useState();
  const [mappedTable, setMappedTable] = useState([]);
  const [pathAuthorityFile, setPathAuthorityFile] = useState([]);
  const [pathAppFile, setPathAppFile] = useState([]);
  function handleOpenDialogRecordDetailsLogAnalysis(data, index) {
    setOpenDialogRecordDetailLogAnalysis(true);
    setDataDialogDetails(appFileContent);
    setIndexDetails(index);
  }

  function handleCloseDialogRecordDetails() {
    setOpenDialogRecordDetailLogAnalysis(false);
  }

  useEffect(() => {
    if (appFileContent && authorityVehicleFileContent) {
      const dataLocalStorageStr = localStorage.getItem('analysisParameter');
      if (dataLocalStorageStr) {
        const analysisParameter = JSON.parse(dataLocalStorageStr);
        const dataTable = buildTable(
          appFileContent,
          authorityVehicleFileContent,
          analysisParameter
        );
        console.log('dataTable ====>', dataTable);
        setMappedTable(dataTable);
      }
    }
  }, [appFileContent, authorityVehicleFileContent]);
  // #endregion

  useEffect(() => {
    if (isOwnReady) {
      const locACC = [];
      appFileContent
        .map((autho) => autho.authorityPayload)
        .forEach((payload, index) => {
          const location = {
            lng: parseFloat(payload.eventos[0].longitud),
            lat: parseFloat(payload.eventos[0].latitud),
          };
          locACC.push(location);
        });
      setPathAppFile(locACC);
    }
  }, [appFileContent, isOwnReady]);

  useEffect(() => {
    if (isauthorityCompanyReady) {
      const locACC = [];
      authorityVehicleFileContent.forEach((payload, index) => {
        const location = {
          lng: parseFloat(payload.eventos[0].longitud.replace(',', '.')),
          lat: parseFloat(payload.eventos[0].latitud.replace(',', '.')),
        };
        locACC.push(location);
      });
      setPathAuthorityFile(locACC);
    }
  }, [authorityVehicleFileContent, isauthorityCompanyReady]);

  return (
    <div className={`${classes.heightCalc}`}>
      <div className={`w-full ${classes.heightCalcTable}`}>
        <div className={`overflow-auto w-full ${classes.tableExecution}`}>
          <FixedSizeList
            height={availableHeight}
            width="100%"
            itemSize={rowHeight}
            itemCount={(mappedTable || []).length}
            itemData={{
              itemsArray: mappedTable || [],
              handleOpenDialogRecordDetailsLogAnalysis,
              style: classes,
            }}
          >
            {Row}
          </FixedSizeList>
        </div>
      </div>

      <div className="w-full">
        <Map
          pathAppFile={pathAppFile}
          authorityAppFileContent={authorityVehicleFileContent}
          pathAuthorityFile={pathAuthorityFile}
          mapChangedTimestamp={mapChangedTimestamp}
          setMapChangedTimestamp={setMapChangedTimestamp}
          mappedTable={mappedTable}
        />
      </div>

      {openDialogRecordDetailLogAnalysis && (
        <DialogRecordDetailLogAnalysis
          {...{
            open: openDialogRecordDetailLogAnalysis,
            handleClose: handleCloseDialogRecordDetails,
            dataDialogDetails,
            indexDetails,
          }}
        />
      )}
    </div>
  );
}
