import { useState, useContext } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';

import {
  Icon,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
} from '@mui/material';
import { makeStyles } from '@mui/styles';
import DialogStatistics from './dialogs/DialogStatistics';
import DialogRecordDetail from './dialogs/DialogRecordDetail';
import AnalysisContext from './Analysis-context/AnalysisContext';

const useStyles = makeStyles({
  tableExecution: {
    '& th': {
      padding: '0',
      position: 'relative',
    },
    '& td': {
      padding: '0',
      position: 'relative',
    },
  },
  tableIcon: {
    position: 'relative',
    '& span': {
      position: 'absolute',
      left: '0px',
      fontSize: '26px',
      top: '-1px',
    },
    '& p': {
      marginTop: '14px',
      marginLeft: '14px',
      fontSize: '1rem',
    },
  },
  check: {
    color: '#66bb6a',
  },
  error: {
    color: '#f44336',
  },
  warning: {
    color: '#ff9a00',
  },
  marginTopTable: {
    marginTop: '165px',
  },
  marginTopTableTwo: {
    marginTop: '85px',
  },
  /**/
  sheet: {
    touchAction: 'none',
  },
  btnDark: {
    padding: '6px',
    minWidth: 'auto',
    background: 'transparent',
    border: '2px solid #252f3e',
    color: '#252f3e',
    borderRadius: '100%',
    '&:hover': {
      color: 'white',
    },
  },
});

const recordStates = {
  PERSISTED: 'save',
  SENT: 'check',
  ERROR: 'error',
  WARNING: 'warning',
  NOTE: 'note',
};

function AnalysisAppSidebarContent(props) {
  const { appFileContent } = useContext(AnalysisContext);
  const { t } = useTranslation('analysisPage');
  const classes = useStyles();

  function createData(type, seq, state, timeSent, timestamp, vel, doors, input, output, desvi) {
    return { type, seq, state, timeSent, timestamp, vel, doors, input, output, desvi };
  }

  const dopDistancePermissibleErrors = (appFileContent || []).map((data) => {
    if (data.type === 'RECORD') {
      const {
        state,
        recordSeq,
        timestamp,
        payload: {
          backDoorInput,
          backDoorOpen,
          backDoorOutput,
          frontDoorInput,
          frontDoorOpen,
          frontDoorOutput,
          geolocationPosition: {
            coords: { speed },
          },
        },
      } = data;
      return createData(
        data.type,
        recordSeq,
        recordStates[state],
        state === 'PERSISTED' ? null : 0.2,
        timestamp,
        speed,
        `${frontDoorOpen ? 'DA' : 'DC'}${backDoorOpen ? 'TA' : 'TC'}`,
        `${frontDoorInput}/${backDoorInput}`,
        `${frontDoorOutput}/${backDoorOutput}`,
        0.3
      );
    }
    return data;
  });

  const optionsDateTimeLine = {
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false,
  };

  // #region dialog statistics
  const [openDialogStatistics, setOpenDialogStatistics] = useState(false);

  function handleOpenDialogStatistics() {
    setOpenDialogStatistics(true);
  }

  function handleCloseDialogStatistics() {
    setOpenDialogStatistics(false);
  }
  // #endregion

  // #region dialog statistics
  const [openDialogRecordDetails, setOpenDialogRecordDetails] = useState(false);
  const [dataDialogDetails, setDataDialogDetails] = useState();
  const [indexDetails, setIndexDetails] = useState();

  function handleOpenDialogRecordDetails(data, index) {
    setOpenDialogRecordDetails(true);
    setDataDialogDetails(appFileContent);
    setIndexDetails(index);
  }

  function handleCloseDialogRecordDetails() {
    setOpenDialogRecordDetails(false);
  }
  // #endregion

  return (
    <motion.div
      initial={{ y: 50, opacity: 0 }}
      animate={{ y: 0, opacity: 1, transition: { delay: 0.4 } }}
      className="flex-auto p-10"
    >
      <div className="flex items-center justify-between mb-10">
        <Typography className="text-lg">
          <b>{t('ANALYSIS_CONTENT_TITLE')}</b>
        </Typography>
        <Button
          variant="contained"
          className={`${classes.btnDark}`}
          onClick={() => {
            handleOpenDialogStatistics();
          }}
        >
          <Icon>show_chart</Icon>
        </Button>
      </div>

      <TableContainer>
        <Table size="small" className={`${classes.tableExecution}`}>
          <TableHead>
            <TableRow>
              <TableCell align="center">#</TableCell>
              <TableCell />

              <TableCell>{t('ANALYSIS_CONTENT_HOUR')}</TableCell>
              <TableCell>{t('ANALYSIS_CONTENT_VEL')}</TableCell>
              <TableCell>{t('ANALYSIS_CONTENT_DOORS')}</TableCell>
              <TableCell align="center">
                <Icon>keyboard_arrow_up</Icon>
              </TableCell>
              <TableCell align="center">
                <Icon>keyboard_arrow_down</Icon>
              </TableCell>
              <TableCell>{t('ANALYSIS_CONTENT_DESV')}</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {dopDistancePermissibleErrors.map((row, index) => {
              const numberTable = index + 1;

              return (
                <TableRow
                  key={index}
                  hover
                  sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                  onClick={() => {
                    handleOpenDialogRecordDetails(row, index);
                  }}
                >
                  <TableCell align="center">{numberTable}</TableCell>
                  <TableCell
                    className={`${row.state === 'check' ? classes.tableIcon : 'text-center'}`}
                  >
                    {row.state === 'check' && <Icon className={`${classes.check}`}>check</Icon>}
                    {row.state === 'error' && <Icon className={`${classes.error}`}>error</Icon>}
                    {row.state === 'warning' && (
                      <Icon className={`${classes.warning}`}>warning</Icon>
                    )}
                    {row.state === 'save' && <Icon>save</Icon>}
                    <Typography>{row.timeSent ? `${row.timeSent}s` : ''}</Typography>
                  </TableCell>
                  <TableCell>
                    {new Date(row.timestamp).toLocaleString(
                      navigator.language,
                      optionsDateTimeLine
                    )}
                  </TableCell>
                  <TableCell>{row.vel}</TableCell>
                  <TableCell>{row.doors}</TableCell>
                  <TableCell align="center">{row.input}</TableCell>
                  <TableCell align="center">{row.output}</TableCell>
                  <TableCell align="center">{row.desvi}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
      {openDialogStatistics && (
        <DialogStatistics
          {...{ open: openDialogStatistics, handleClose: handleCloseDialogStatistics }}
        />
      )}
      {openDialogRecordDetails && (
        <DialogRecordDetail
          {...{
            open: openDialogRecordDetails,
            handleClose: handleCloseDialogRecordDetails,
            dataDialogDetails,
            indexDetails,
          }}
        />
      )}
    </motion.div>
  );
}

export default AnalysisAppSidebarContent;
