import { useState, useEffect, useContext } from 'react';

import { useTranslation } from 'react-i18next';
import { Button, Icon, Typography, TextField, InputAdornment } from '@mui/material';
import { makeStyles } from '@mui/styles';
import TravelContext from '../travel-context/TravelContext';

const useStyles = makeStyles({
  animationBtn: {
    overflow: 'hidden',
  },
  water: {
    position: 'absolute',
    left: '0',
    top: '0',
    width: '100%',
    height: '100%',
    transition: 'all .3s',
    '& .water_wave': {
      width: '200%',
      position: 'absolute',
      bottom: '100%',
      '&_back': {
        right: '0',
        animation: '$waveBack 1400ms infinite linear',
      },
      '&_front': {
        left: '0',
        marginBottom: '-1px',
        animation: '$waveFront 700ms infinite linear',
      },
    },
  },

  '@keyframes waveFront': {
    '100%': {
      transform: 'translate(-50%, 0)',
    },
  },
  '@keyframes waveBack': {
    '100%': {
      transform: 'translate(50%, 0)',
    },
  },
  modInputsCards: {
    '& input': {
      padding: '5px 0',
    },
  },
  btnStateDoor: {
    transition: 'all 0.5s ease',
  },
  btnStateDoorActive: {
    width: 'auto',
    position: 'absolute',
    padding: '5px',
    minWidth: 'auto',
    margin: '0 auto',
    left: '0',
    right: '0',
    top: '0',
    zIndex: '1',
    backgroundColor: '#ffffffc4 !important',
    color: '#1b2330',
  },
});

export default function BtnActionsExecution(props) {
  const { type, accumulatorTimeThreshold } = props;
  const {
    keyboardDetected,
    handleAddCountDoorEntry,
    handleAddCountDoorExit,
    topDoor,
    botDoor,
    stateCountTopDoor,
    stateCountBotDoor,
    timeSecondsTop,
    timeSecondsBot,
    stateDoor,
    handleDoor,
    tripTempState,
  } = useContext(TravelContext);
  const { t } = useTranslation('travelsPage');
  const classes = useStyles();

  const [percentageTop, setPercentageTop] = useState(100);
  const [percentageBot, setPercentageBot] = useState(100);

  useEffect(() => {
    // console.log('useEffect timeSecondsTop',`parseInt((${timeSecondsTop} / ${accumulatorTimeThreshold})*100, 10)`,  parseInt((timeSecondsTop / accumulatorTimeThreshold)*100, 10));
    setPercentageTop(parseInt((timeSecondsTop / accumulatorTimeThreshold) * 100, 10));
  }, [timeSecondsTop]);

  useEffect(() => {
    setPercentageBot(parseInt((timeSecondsBot / accumulatorTimeThreshold) * 100, 10));
  }, [timeSecondsBot]);

  const [backgroundColorWater, setBackgroundColorWater] = useState({
    top: { one: '#22d3ee', two: '#C7EEFF' },
    bot: { one: '#22d3ee', two: '#C7EEFF' },
  });

  useEffect(() => {
    const validate = type === 'top' ? percentageTop : percentageBot;

    if (validate >= 0 && validate <= 25) {
      setBackgroundColorWater({
        ...backgroundColorWater,
        [type]: { one: '#62d4ab', two: '#C7EEFF' },
      });
    } else if (validate >= 25 && validate <= 50) {
      setBackgroundColorWater({
        ...backgroundColorWater,
        [type]: { one: '#ffc979', two: '#62d4ab' },
      });
    } else if (validate >= 50 && validate <= 75) {
      setBackgroundColorWater({
        ...backgroundColorWater,
        [type]: { one: '#ff9800', two: '#ffc979' },
      });
    } else if (validate >= 75 && validate <= 100) {
      setBackgroundColorWater({
        ...backgroundColorWater,
        [type]: { one: '#f44336', two: '#ff9800' },
      });
    }
  }, [percentageTop, percentageBot]);

  return (
    <div className="flex">
      <Button
        variant="contained"
        className={`flex-1 flex-wrap p-0 place-content-center justify-center ${classes.animationBtn}`}
        onClick={() => {
          handleAddCountDoorEntry(type, null);
        }}
      >
        <svg x="0px" y="0px" style={{ display: 'none' }}>
          <symbol id="wave">
            <path d="M420,20c21.5-0.4,38.8-2.5,51.1-4.5c13.4-2.2,26.5-5.2,27.3-5.4C514,6.5,518,4.7,528.5,2.7c7.1-1.3,17.9-2.8,31.5-2.7c0,0,0,0,0,0v20H420z" />
            <path d="M420,20c-21.5-0.4-38.8-2.5-51.1-4.5c-13.4-2.2-26.5-5.2-27.3-5.4C326,6.5,322,4.7,311.5,2.7C304.3,1.4,293.6-0.1,280,0c0,0,0,0,0,0v20H420z" />
