        <div
          id="water"
          className={`${classes.water}`}
          style={{
            transform: `translate(0, ${type === 'top' ? percentageTop : percentageBot}%)`,
            background: `${backgroundColorWater[type].one}`,
          }}
        >
          <svg
            viewBox="0 0 560 20"
            className="water_wave water_wave_back"
            fill={backgroundColorWater[type].two}
          >
            <use href="#wave" />
          </svg>
          <svg
            viewBox="0 0 560 20"
            className="water_wave water_wave_front"
            fill={backgroundColorWater[type].one}
          >
            <use href="#wave" />
          </svg>
        </div>

        <div className="z-10">
          <Icon>remove_circle_outline</Icon>
          <Typography className="w-full">
            {type === 'top' ? tripTempState.frontDoorOutput : tripTempState.backDoorOutput}
          </Typography>
        </div>
      </Button>
    </div>
  );
