/* REACT CORE */
import React, { useState, useEffect } from 'react';
/* UI CORE - MATERIAL-UI */

/* MAP UI core */
import GoogleMap from 'google-map-react';
/* @FUSE COMPONENTS */
import { mapOptions } from './mapOptions';
import Marker from './Marker';

export function Map(props) {
  const {
    pathAppFile,
    pathAuthorityFile,
    mapChangedTimestamp,
    setMapChangedTimestamp,
    appFileContent,
    authorityAppFileContent,
  } = props;
  const [appFilePolyline, setAppFilePolyline] = useState();
  const [authorityFilePolyline, setAthorityFilePolyline] = useState();

  const [appEvents, setAppEvents] = useState([]);
  const [authEvents, setAuthEvents] = useState([]);

  const apiIsLoaded = (map, maps) => {
    const appFilePolyline = new maps.Polyline({
      path: pathAppFile,
      geodesic: false,
      strokeColor: '#7B2AD7',
      strokeOpacity: 1,
      strokeWeight: 3,
    });
    const authorityPolyline = new maps.Polyline({
      path: pathAuthorityFile,
      geodesic: false,
      strokeColor: '#2d7fe2',
      strokeOpacity: 1,
      strokeWeight: 3,
    });

    appFilePolyline.setMap(map);
    setAppFilePolyline(appFilePolyline);

    authorityPolyline.setMap(map);
    setAthorityFilePolyline(authorityPolyline);
  };

  useEffect(() => {
    setMapChangedTimestamp(Date.now());
  }, [appFilePolyline, authorityFilePolyline]);

  useEffect(() => {
    const currentAppContent = [];
    console.log('appFileContent', appFileContent);
    if (appFileContent) {
      for (const appRow of appFileContent) {
        const { authorityPayload, type } = appRow;
        if (type === 'RECORD') {
          for (const event of authorityPayload.eventos) {
            const formattedEvent = formatEventData(event);
            currentAppContent.push(formattedEvent);
          }
        }
      }
    }
    currentAppContent.sort((a, b) => b.timestamp - a.timestamp);
    setAppEvents(currentAppContent);
  }, [appFileContent]);

  useEffect(() => {
    const currentAuthContent = [];
    if (authorityAppFileContent) {
      for (const authRow of authorityAppFileContent) {
        for (const event of authRow.eventos) {
          const formattedEvent = formatEventData(event);
          currentAuthContent.push(formattedEvent);
        }
      }
    }
    currentAuthContent.sort((a, b) => b.timestamp - a.timestamp);
    setAuthEvents(currentAuthContent);
  }, [authorityAppFileContent]);

  const formatEventData = (event) => {
    const eventFormatted = {};
    const year = event.fecha.slice(0, 4);
    const month = event.fecha.slice(4, 6);
    const day = event.fecha.slice(6, 8);
    const hour = event.hora.slice(0, 2);
    const minute = event.hora.slice(2, 4);
    const second = event.hora.slice(4, 6);
    const timestamp = new Date(
      `${year}-${month}-${day}T${hour}:${minute}:${second}-05:00`
    ).getTime();

    const backDoor = event.puerta.find(({ idPuerta }) => idPuerta === 1);
    const frontDoor = event.puerta.find(({ idPuerta }) => idPuerta === 0);

    eventFormatted.backDoor = backDoor;
    eventFormatted.frontDoor = frontDoor;

    eventFormatted.timestamp = timestamp;
    eventFormatted.dop = parseFloat(event.dop.replace(',', '.'));
    eventFormatted.longitude = parseFloat(event.longitud.replace(',', '.'));
    eventFormatted.latitude = parseFloat(event.latitud.replace(',', '.'));

    return eventFormatted;
  };

  const getRecordToDraw = (event, index, events) => {
    const prevRecord = events[index - 1] || {};
    const { frontDoor: prevFrontDoor = {}, backDoor: prevBackDoor = {} } = prevRecord;
    const { frontDoor, backDoor } = event;
    const hasPassengerFlow =
      (frontDoor.ingresos || 0) !== 0 ||
      (frontDoor.salidas || 0) !== 0 ||
      (backDoor.ingresos || 0) !== 0 ||
      (backDoor.salidas || 0) !== 0;
    const hasDoorChange = prevRecord
      ? prevFrontDoor.estado !== frontDoor.estado || prevBackDoor.estado !== backDoor.estado
      : false;
    return hasPassengerFlow || hasDoorChange;
  };
  const buildMarker = (type, eventData, index) => {
    const { frontDoor, backDoor, latitude, longitude, timestamp } = eventData;
    const { ingresos: frontDoorInput, salidas: frontDoorOutput, estado: frontDoorOpen } = frontDoor;
    const { ingresos: backDoorInput, salidas: backDoorOutput, estado: backDoorOpen } = backDoor;
    const contentText = `${type}: ${
      !frontDoorOpen ? 'DA' : 'DC'
    }+${frontDoorInput}-${frontDoorOutput}, ${
      !backDoorOpen ? 'TA' : 'TC'
    }+${backDoorInput}-${backDoorOutput}`;
    return (
      <Marker
        key={`${index}-${type}`}
        {...{ lat: latitude, lng: longitude, type }}
        text={contentText}
      />
    );
  };

  return (
    <div id="listing_parent" className="w-full h-full">
      <GoogleMap
        bootstrapURLKeys={{
          key: process.env.REACT_APP_MAP_KEY,
        }}
        options={mapOptions}
        defaultZoom={12}
        defaultCenter={[6.236118, -75.580598]}
        yesIWantToUseGoogleMapApiInternals
        onGoogleApiLoaded={({ map, maps }) => apiIsLoaded(map, maps)}
      >
        {appEvents
          .filter((event, index, events) => getRecordToDraw(event, index, events))
          .map((event, index) => buildMarker('APP', event, index))}

        {authEvents
          .filter((event, index, events) => getRecordToDraw(event, index, events))
          .map((event) => buildMarker('AUTH', event))}
      </GoogleMap>
    </div>
  );
}

function mapPropsAreEqual(prevProps, nextProps) {
  return prevProps.mapChangedTimestamp === nextProps.mapChangedTimestamp;
}

const MemoizedMap = React.memo(Map, mapPropsAreEqual);
export default MemoizedMap;
