import { useState, useEffect, useContext } from 'react';
import _ from '@lodash';
import PropTypes from 'prop-types';
import {
  Typography,
  Box,
  Button,
  Icon,
  InputAdornment,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
} from '@mui/material';
import { useTranslation } from 'react-i18next';
import { Controller, useForm } from 'react-hook-form';
import { useSnackbar } from 'notistack';
import JSZip from 'jszip';
import { makeStyles } from '@mui/styles';
import AnalysisContext from '../../Analysis-context/AnalysisContext';

const pako = require('pako');

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box className="p-10">
          <div>{children}</div>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

const useStyles = makeStyles({
  inputNumber: {
    '& input[type=number]::-webkit-inner-spin-button, input[type=number]::-webkit-outer-spin-button':
    {
      '-webkit-appearance': 'none',
      margin: '0',
    },

    '& input[type=number]': {
      '-moz-appearance': 'textfield',
    },
  },
  tableDistance: {
    '& tr td:last-child': {
      width: '80px',
      maxWidth: '80px',
    },
    '& tr th:last-child': {
      width: '80px',
      maxWidth: '80px',
    },
  },
  inputFile: {
    background: '#f3f3f3',
  },
});

export default function Parameter(props) {
  const {
    appFileContent,
    authorityAppFileContent,
    authorityVehicleFileContent,
    setAppFileContent,
    setAuthorityAppFileContent,
    setAuthorityVehicleFileContent,
    setAuthorityAppFileContentName,
    setAuthorityVehicleFileContentName,
    setAppFileContentName,
    authorityLength,
    setAuthorityLength,
    ownLength,
    setOwnLength,
    isAuthorityReady,
    setIsAuthorityReady,
    isauthorityCompanyReady,
    setAuthorityCompanyReady,
    isOwnReady,
    setIsOwnReady,
    authorityAppFileContentName,
    authorityVehicleFileContentName,
    appFileContentName,
  } = useContext(AnalysisContext);
  const { t } = useTranslation('analysisPage');
  const classes = useStyles();
  const { enqueueSnackbar } = useSnackbar();

  const methods = useForm({
    mode: 'onChange',
    defaultValues: {},
  });
  const { reset, watch, control, onChange, formState } = methods;

  const dataLocalStorage = localStorage.getItem('analysisParameter');

  const [permissibleErrors, setPermissibleErrors] = useState({
    frontDoorEntrance: 3,
    frontDoorExit: 3,
    backDoorEntrance: 3,
    backDoorExit: 3,
    times: 15,
  });

  const handleChangePermissibleErrors = (fielName) => (evt) => {
    const data = isNaN(parseInt(evt.target.value, 10))
      ? 0
      : parseInt(evt.target.value, 10) < 0
        ? 0
        : parseInt(evt.target.value, 10);
    setPermissibleErrors({ ...permissibleErrors, [fielName]: data });
    localStorage.setItem(
      'analysisParameter',
      JSON.stringify({
        ...JSON.parse(dataLocalStorage),
        permissibleErrors: { ...permissibleErrors, [fielName]: data },
        dopDistancePermissibleErrors
      })
    );
  };

  // #region dopDistancePermissibleErrors table parameter
  const [tableParameter, setTableParameter] = useState({ dopInitial: undefined, dopFinal: undefined, radio: undefined });

  const [dopDistancePermissibleErrors, setDopDistancePermissibleErrors] = useState([
    createData(0, 1, 3),
    createData(1, 2, 10),
    createData(2, 5, 50),
    createData(5, 10, 100),
    createData(10, 20, 300),
    createData(20, 100, 500),
  ]);

  const handleTableParameter = (fielName) => (evt) => {

    const data = parseFloat(evt.target.value) < 0
      ? 0
      : (evt.nativeEvent.data === 'e' || evt.nativeEvent.data === 'E')
        ? 0
        : evt.target.value.length > 0 && isNaN(parseFloat(evt.target.value))
          ? 0
          : evt.target.value;

    setTableParameter({ ...tableParameter, [fielName]: data });
  };

  function createData(dopInitial, dopFinal, radio, editTable) {
    return { dopInitial, dopFinal, radio, editTable };
  }

  function editRow() {
    setTableParameter({ dopInitial: undefined, dopFinal: undefined, radio: undefined });
    const dataDopDistancePermissibleErrors = _.cloneDeep(dopDistancePermissibleErrors);
    dataDopDistancePermissibleErrors.push(createData(undefined, undefined, undefined, true));
    setDopDistancePermissibleErrors(dataDopDistancePermissibleErrors);
  }

  function addRow(tableParameterToModify, index) {
    const dataDopDistancePermissibleErrors = _.cloneDeep(dopDistancePermissibleErrors);
    const message = valdiateDOP(tableParameterToModify, dataDopDistancePermissibleErrors).filter(error => error !== null);

    if (message && message.length > 0) {
      const textMessage = message.filter((c, index) => {
        return message.indexOf(c) === index;
      });
      enqueueSnackbar(t(textMessage[0]), { variant: 'error' });
    } else {
      dataDopDistancePermissibleErrors[index] = createData(
        tableParameterToModify.dopInitial,
        tableParameterToModify.dopFinal,
        tableParameterToModify.radio,
        false
      );
      setDopDistancePermissibleErrors(dataDopDistancePermissibleErrors);
      localStorage.setItem(
        'analysisParameter',
        JSON.stringify({ ...JSON.parse(dataLocalStorage), dopDistancePermissibleErrors: dataDopDistancePermissibleErrors })
      );
    }
  }

  function removeRow(index) {
    const dataDopDistancePermissibleErrors = _.cloneDeep(dopDistancePermissibleErrors);
    const filterData = dataDopDistancePermissibleErrors.filter((d, i) => i !== index);
    localStorage.setItem(
      'analysisParameter',
      JSON.stringify({ ...JSON.parse(dataLocalStorage), dopDistancePermissibleErrors: filterData })
    );
    setDopDistancePermissibleErrors(filterData);
  }

  function validatePassenger(val) {
    if (val > 999) {
      return false;
    }
    return true;
  }

  function _base64ToArrayBuffer(base64) {
    const binaryString = window.atob(base64);
    const len = binaryString.length;
    const bytes = new Uint8Array(len);
    for (let i = 0; i < len; i++) {
      bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes.buffer;
  }

  function handleFileChange(file, type) {
    localStorage.setItem(
      'analysisParameter',
      JSON.stringify({
        ...JSON.parse(dataLocalStorage),
        permissibleErrors,
        dopDistancePermissibleErrors
      })
    );
    switch (type) {
      case 'appFileContent':
        setAppFileContent([]);

        if (!file) return;
        const fileReader = new FileReader();
        fileReader.onload = () => {
          const fileContent = fileReader.result;
          const dataToDecompress = _base64ToArrayBuffer(fileContent);
          const decompress = pako.inflate(dataToDecompress, { to: 'string' });
          const dataFormatted = JSON.parse(decompress);
          setAppFileContent(dataFormatted);
          setOwnLength(dataFormatted.length);
          setIsOwnReady(true);
        };
        fileReader.readAsText(file);
        break;
      case 'authorityAppFileContentName':
        setAuthorityAppFileContent([]);
        if (!file) return;
        JSZip.loadAsync(file).then(function (zip) {
          let counter = 0;
          zip.forEach(function (relativePath, zipEntry) {
            counter += 1;
            zipEntry.async('uint8array').then((uintarr) => {
              const byteArray = new Uint8Array(uintarr);
              const uncompressed = pako.inflate(byteArray, { to: 'string' });
              setAuthorityAppFileContent((authority) => [...authority, JSON.parse(uncompressed)]);
            });
          });
          setAuthorityLength(counter);
          setIsAuthorityReady(false);
        });
        break;
      case 'authorityVehicleFileContent':
        setAuthorityVehicleFileContent([]);
        if (!file) return;
        JSZip.loadAsync(file).then(function (zip) {
          let counter = 0;
          zip.forEach(function (relativePath, zipEntry) {
            counter += 1;
            zipEntry.async('uint8array').then((uintarr) => {
              const byteArray = new Uint8Array(uintarr);
              const uncompressed = pako.inflate(byteArray, { to: 'string' });
              setAuthorityVehicleFileContent((authorityAppFileContent) => [
                ...authorityAppFileContent,
                JSON.parse(uncompressed),
              ]);
            });
          });
          setAuthorityLength(counter);
          setAuthorityCompanyReady(false);
        });
        break;
      default:
        break;
    }
  }

  function valdiateDOP(newRoowToAdd, currentRows) {
    return (currentRows || []).map((row) => {

      if (newRoowToAdd.dopInitial === undefined || newRoowToAdd.dopFinal === undefined || newRoowToAdd.radio === undefined) {
        return 'ALL_FIELDS_ARE_REQUIRED';
      }
      if (newRoowToAdd.dopInitial === "" || newRoowToAdd.dopFinal === "" || newRoowToAdd.radio === "") {
        return 'ALL_FIELDS_ARE_REQUIRED';
      }
      if (
        //el nuevo rango de DOP no puede estar contenido en otro rango
        //newRoowToAdd.dopInitial nboo puede estar adentro de otro rango
        //newRoowToAdd.dopFinal nboo puede estar adentro de otro rango

        newRoowToAdd.dopInitial >= row.dopInitial &&
        newRoowToAdd.dopInitial <= row.dopFinal
      ) {
        return 'DOP_INVALID';
      }
      if (parseFloat(newRoowToAdd.dopFinal) < parseFloat(newRoowToAdd.dopInitial)) {
        // el dopFinal debe ser siempre mayor al inicial // FINAL DOP MUST BE GREATER THAN INITIAL
        return 'FINAL_DOP_MUST_BE_GREATER_THAN_INITIAL';
      }
      if (parseFloat(newRoowToAdd.dopInitial) < 0 || parseFloat(newRoowToAdd.dopInitial) > 100) {
        // el DOP inicial debe ser mayor o igual a 0 y menor o igual a 100
        return parseFloat(newRoowToAdd.dopInitial) < 0 ? 'INITIAL_DOP_MUST_BE_GREATER_OR_EQUAL_TO_0' : 'INITIAL_DOP_MUST_BE_LESS_THAN_OR_EQUAL_TO_100';
      }
      if (parseFloat(newRoowToAdd.dopFinal) < 0 || parseFloat(newRoowToAdd.dopFinal) > 100) {
        // el DOP final debe ser mayor o igual a 0 y menor o igual a 100
        return parseFloat(newRoowToAdd.dopFinal) < 0 ? 'FINAL_DOP_MUST_BE_GREATER_OR_EQUAL_TO_0' : 'FINAL_DOP_MUST_BE_LESS_THAN_OR_EQUAL_TO_100';
      }

      if (parseFloat(newRoowToAdd.radio) < 0 || parseFloat(newRoowToAdd.radio) > 1000) {
        // el radio debe ser mayor o igual a 0 y menor o igual a 1000
        return parseFloat(newRoowToAdd.radio) < 0 ? 'THE_RADIO_MUST_BE_GREATER_OR_EQUAL_TO_0' : 'THE_RADIO_MUST_BE_LESS_THAN_OR_EQUAL_TO_1000';
      }

      return null;
    });
  }

  useEffect(() => {
    if (appFileContent.length > 0 && ownLength === appFileContent.length) {
      setIsOwnReady(true);
    }
  }, [ownLength, appFileContent]);

  useEffect(() => {
    if (authorityAppFileContent.length > 0 && authorityLength === authorityAppFileContent.length) {
      setIsAuthorityReady(true);
    }
  }, [authorityLength, authorityAppFileContent]);

  useEffect(() => {
    if (
      authorityVehicleFileContent.length > 0 &&
      authorityLength === authorityVehicleFileContent.length
    ) {
      setAuthorityCompanyReady(true);
    }
  }, [authorityLength, authorityVehicleFileContent]);

  useEffect(() => {
    if (dataLocalStorage) {
      const data = JSON.parse(dataLocalStorage);
      setPermissibleErrors(data.permissibleErrors);
      setDopDistancePermissibleErrors(data.dopDistancePermissibleErrors);
    }
  }, [dataLocalStorage]);

  // #endregion

  return (
    <div>
      <div className="btns flex flex-wrap w-full justify-between">
        <div className="flex-auto sm:flex-1 pr-10 sm:w-auto w-full mb-10 sm:mb-0">
          <Typography className="leading-none mb-10">
            <b>{t('PARAMETER_SIMULATED_TRAVEL_APP_FILE')}:</b>
          </Typography>
          <Controller
            name="appFileContent"
            control={control}
            render={({ field: { onChange, value } }) => (
              <label
