              >
                <input
                  accept=".neb"
                  className="hidden"
                  id="appFileContent"
                  type="file"
                  onChange={(e) => {
                    handleFileChange(e.target.files[0], 'appFileContent');
                    setAppFileContentName((e.target.files[0] || {}).name);
