                />
                <Icon fontSize="large" color="action" className="mr-5">
                  cloud_upload
                </Icon>
                {!isOwnReady && appFileContent.length > 0 ? (
                  <CircularProgress size={25} />
                ) : (
                  <Typography>{appFileContentName || t('PARAMETER_NO_FILE')}</Typography>
                )}
              </label>
            )}
          />
        </div>

        <div className="flex-auto sm:flex-1 pr-10 sm:w-auto w-full mb-10 sm:mb-0">
          <Typography className="leading-none mb-10">
            <b>{t('PARAMETER_SIMULATED_TRIP_AUTHORITY_FILE')}:</b>
          </Typography>
          <Controller
            name="authorityAppFileContentName"
            control={control}
            render={({ field: { onChange, value } }) => (
              <label
                htmlFor="authorityAppFileContentName"
                className={`productImageUpload flex items-center justify-center relative rounded-16 overflow-hidden cursor-pointer shadow hover:shadow-lg p-10 ${classes.inputFile}`}
              >
                <input
                  accept=".zip,.rar,.7zip"
                  className="hidden"
                  id="authorityAppFileContentName"
                  type="file"
                  onChange={(e) => {
                    handleFileChange(e.target.files[0], 'authorityAppFileContentName');
                    setAuthorityAppFileContentName((e.target.files[0] || {}).name);
                  }}
                />
                {!isAuthorityReady && authorityAppFileContent.length > 0 ? null : (
                  <Icon fontSize="large" color="action" className="mr-5">
                    cloud_upload
                  </Icon>
                )}
                {!isAuthorityReady && authorityAppFileContent.length > 0 ? (
                  <CircularProgress size={25} />
                ) : (
                  <Typography>{authorityAppFileContentName || t('PARAMETER_NO_FILE')}</Typography>
                )}
              </label>
            )}
          />
        </div>

        <div className="flex-auto sm:flex-1 pr-10 sm:w-auto w-full mb-10 sm:mb-0">
          <Typography className="leading-none mb-10">
            <b>{t('PARAMETER_TRAVEL_AUTHORITY_FILE_TRANSPORT')}:</b>
          </Typography>
          <Controller
            name="authorityVehicleFileContent"
            control={control}
            render={({ field: { onChange, value } }) => (
              <label
                htmlFor="authorityVehicleFileContent"
                className={`productImageUpload flex items-center justify-center relative rounded-16 overflow-hidden cursor-pointer shadow hover:shadow-lg p-10 ${classes.inputFile}`}
              >
                <input
                  accept=".zip,.rar,.7zip"
                  className="hidden"
                  id="authorityVehicleFileContent"
                  type="file"
                  onChange={(e) => {
                    handleFileChange(e.target.files[0], 'authorityVehicleFileContent');
                    setAuthorityVehicleFileContentName((e.target.files[0] || {}).name);
                  }}
                />
                {!isauthorityCompanyReady && authorityVehicleFileContent.length > 0 ? null : (
                  <Icon fontSize="large" color="action" className="mr-5">
                    cloud_upload
                  </Icon>
                )}
                {!isauthorityCompanyReady && authorityVehicleFileContent.length > 0 ? (
                  <CircularProgress size={25} />
                ) : (
                  <Typography>
                    {authorityVehicleFileContentName || t('PARAMETER_NO_FILE')}
                  </Typography>
                )}
              </label>
            )}
          />
        </div>
      </div>

      <div className="distance w-full mt-20">
        <div className="flex items-center justify-between">
          <Typography>
            <b>{t('PARAMETER_DISTANCES_FOR_EVENT')}</b>
          </Typography>
          <Button
            onClick={() => {
              editRow();
            }}
          >
            <Icon>add_circle</Icon>
          </Button>
        </div>

        <div className="tableDistance">
          <TableContainer component={Paper}>
            <Table size="small" className={`${classes.tableDistance}`}>
              <TableHead>
                <TableRow>
                  <TableCell align="center">
                    <b>{t('PARAMETER_INITIAL_DOP')}</b>
                  </TableCell>
                  <TableCell align="center">
                    <b>{t('PARAMETER_FINAL_DOP')}</b>
                  </TableCell>
                  <TableCell align="center">
                    <b>{t('PARAMETER_RADIO')}</b>
                  </TableCell>
                  <TableCell />
                </TableRow>
              </TableHead>
              <TableBody>
                {(dopDistancePermissibleErrors || [])
                  .filter((u) => u)
                  .sort((a, b) => !a.editTable ? a.dopInitial - b.dopInitial : 0)
                  .map((row, index) => {
                    return row.editTable ? (
                      <TableRow
                        key={`events-${index}`}
                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                        className={`${classes.inputNumber}`}
                      >
                        <TableCell align="center">
                          <TextField
                            id="dopInitial"
                            autoFocus
                            fullWidth
                            sx={{ m: 1 }}
                            type="number"
                            className="m-0"
                            value={tableParameter.dopInitial}
                            variant="standard"
                            onChange={handleTableParameter('dopInitial')}
                          />
                        </TableCell>
                        <TableCell align="center">
                          <TextField
                            id="dopFinal"
                            fullWidth
                            sx={{ m: 1 }}
                            type="number"
                            className="m-0"
                            value={tableParameter.dopFinal}
                            variant="standard"
                            onChange={handleTableParameter('dopFinal')}
                          />
                        </TableCell>
                        <TableCell align="center">
                          <TextField
                            id="radio"
                            fullWidth
                            sx={{ m: 1 }}
                            type="number"
                            className="m-0"
                            value={tableParameter.radio}
                            variant="standard"
                            onChange={handleTableParameter('radio')}
                          />
                        </TableCell>
                        <TableCell align="right">
                          <Button
                            onClick={() => {
                              addRow(tableParameter, index);
                            }}
                          >
                            <Icon>check_circle</Icon>
                          </Button>
                        </TableCell>
                      </TableRow>
                    ) : (
                      <TableRow
                        key={`events-${index}`}
                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                      >
                        <TableCell align="center">{row.dopInitial}</TableCell>
                        <TableCell align="center">{row.dopFinal}</TableCell>
                        <TableCell align="center">{row.radio}</TableCell>
                        <TableCell align="right">
                          <Button
                            onClick={() => {
                              removeRow(index);
                            }}
                          >
                            <Icon>remove_circle</Icon>
                          </Button>
                        </TableCell>
                      </TableRow>
                    )
                  }

                  )}
              </TableBody>
            </Table>
