      </div>

      <div className="time w-full mt-20">
        <Typography className="mb-10">
          <b>{t('PARAMETER_TIME_SECONDS')}</b>
        </Typography>
        <div className="error">
          <div className={`w-full sm:w-1/2 pr-0 sm:pr-10 flex items-center ${classes.inputNumber}`}>
            <TextField
              id="times"
              error={!validatePassenger((permissibleErrors || {}).times)}
              helperText={!validatePassenger((permissibleErrors || {}).times) ? t('MAX_UNITY') : ''}
              sx={{ m: 1, width: '25ch' }}
              type="number"
              className="m-0"
              value={(permissibleErrors || {}).times}
              onChange={handleChangePermissibleErrors('times')}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">{t('PARAMETER_SECONDS')}</InputAdornment>
                ),
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
}
