import { useEffect, useRef, useState } from 'react';
import _ from '@lodash';
import useNavigatorPermissions from './NavigatorPermissions';

const dopScale = {
  Ideal: {
    min: 0,
    max: 0.9,
  },
  Excellent: {
    min: 1,
    max: 2,
  },
  Good: {
    min: 3,
    max: 5,
  },
  Moderate: {
    min: 6,
    max: 10,
  },
  Fair: {
    min: 11,
    max: 20,
  },
  Poor: {
    min: 21,
    max: 21,
  },
};

const useGeolocation = ({ enableHighAccuracy, maximumAge, timeout } = {}, callback) => {
  const [coordinates, setCoordinates] = useState({
    coords: {
      accuracy: null,
      altitude: null,
      altitudeAccuracy: null,
      heading: null,
      latitude: null,
      longitude: null,
      speed: null,
    },
    timestamp: null,
    error: null,
  });
  const watchId = useRef();
  const { status, error } = useNavigatorPermissions('geolocation');

  function generateDop(
    accuracy,
    { min: minAccuaracy, max: maxAccuaracy },
    { min: minDop, max: maxDop }
  ) {
    /*
    porcentaje dentro del rango de precisión =  (7-3)/(10-3) = 0.57142857142
    porcentaje del rango DOP = (2-1)*0.57142857142+1 = 1.57142857142
    DOP con redondeo = 1.6
    */
    const accuaracyPercentage = (accuracy - minAccuaracy) / (maxAccuaracy - minAccuaracy);
    const dopPercentage = (maxDop - minDop) * accuaracyPercentage + minDop;
    return Math.round(dopPercentage * 10) / 10;
  }
  function convertAccuracyToDop(accuracy) {
    if (accuracy >= 0 && accuracy < 3)
      return generateDop(accuracy, { min: 0, max: 3 }, dopScale.Ideal);
    if (accuracy >= 3 && accuracy < 10)
      return generateDop(accuracy, { min: 3, max: 10 }, dopScale.Excellent);
    if (accuracy >= 10 && accuracy < 50)
      return generateDop(accuracy, { min: 10, max: 50 }, dopScale.Good);
    if (accuracy >= 50 && accuracy < 100)
      return generateDop(accuracy, { min: 50, max: 100 }, dopScale.Moderate);
    // if (accuracy >= 100 && accuracy < 300)
    //   return generateDop(accuracy, { min: 100, max: 300 }, dopScale.Fare);
    // if (accuracy >= 300) return generateDop(accuracy, { min: 300, max: 9999 }, dopScale.Poor);
    return 10.9;
  }

  useEffect(() => {
    let didCancel;
    const updateCoordinates = ({ coords = {}, timestamp }) => {
      const { latitude, longitude, speed, accuracy } = coords;
      const { latitude: coorLat, longitude: coorLon, speed: coorSpeed } = coordinates;
      if (!didCancel) {
        if (
          !_.isEqual(
            { latitude: coorLat, longitude: coorLon, speed: coorSpeed },
            { latitude, longitude, speed }
          )
        ) {
          setCoordinates({
            coords: {
              latitude,
              longitude,
              accuracy,
              dop: convertAccuracyToDop(accuracy),
              speed: parseInt(speed, 10) || 0,
            },
            timestamp,
            error: null,
          });
        }
        if (callback instanceof Function) {
          callback({
            coords: {
              latitude,
              longitude,
              speed: parseInt(speed, 10) || 0,
            },
            timestamp,
            error: null,
          });
        }
      }
    };

    const setError = (error) => {
      if (!didCancel) {
        watchId.current = null;
        setCoordinates({
          coords: {
            latitude: null,
            longitude: null,
            accuracy: null,
            dop: null,
            speed: 0,
          },
          timestamp: null,
          error,
        });
      }
    };

    if (status !== 'granted') {
      if (watchId.current) {
        navigator.geolocation.clearWatch(watchId.current);
        watchId.current = null;
      }
      navigator.geolocation.getCurrentPosition(updateCoordinates, setError, {
        enableHighAccuracy,
        maximumAge,
        timeout,
      });
    } else if (navigator.geolocation && !watchId.current) {
      watchId.current = navigator.geolocation.watchPosition(updateCoordinates, setError, {
        enableHighAccuracy,
        maximumAge,
        timeout,
      });
    }

    return () => {
      if (watchId.current) {
        navigator.geolocation.clearWatch(watchId.current);
      }
      didCancel = true;
    };
  }, [navigator.geolocation, status]);

  return coordinates;
};

export default useGeolocation;
