import { useState, useEffect, useContext, useRef } from 'react';

import { useTranslation } from 'react-i18next';
import { FixedSizeList } from 'react-window';
import { Icon } from '@mui/material';
import { makeStyles } from '@mui/styles';
import Row from './VirtualizedTableExecution/Row';
import TravelContext from '../travel-context/TravelContext';
import Deck from './Deck';

const useStyles = makeStyles({
  tableExecution: {
    '& > div:nth-child(1)': {
      justifyContent: 'space-between',
      paddingTop: '8px',
      paddingBottom: '8px',
      display: 'grid',
      'grid-template-columns': '9% 12.6% 18.8% 10% 13.1% 12.5% 12.5% 11.5%',
    },
    '& li': {
      justifyContent: 'space-between',
      paddingTop: '8px',
      paddingBottom: '8px',
      paddingLeft: '0px',
      paddingRight: '0px',
      display: 'grid',
      'grid-template-columns': '9% 12.6% 18.8% 10% 13.1% 12.5% 12.5% 11.5%',
    },
  },
  noteCell: {
    justifyContent: 'space-between',
    paddingTop: '8px',
    paddingBottom: '8px',
    paddingLeft: '0px',
    paddingRight: '0px',
    display: 'grid',
    'grid-template-columns': '9% 12.6% 18.8% 59.6% !important',
    '& p':{
      whiteSpace: 'nowrap',
      overflow: 'hidden',
      textOverflow: 'ellipsis',
    }
  },
  tableIcon: {
    position: 'relative',
    '& span': {
      position: 'absolute',
      left: '0px',
      fontSize: '24px',
      top: '-1px',
    },
    '& p': {
      marginTop: '16px',
      marginLeft: '21px',
      fontSize: '1rem',
    },
  },
  check: {
    color: '#66bb6a',
  },
  error: {
    color: '#f44336',
  },
  warning: {
    color: '#ff9a00',
  },
  marginTopTable: {
    marginTop: '165px',
  },
  marginTopTableTwo: {
    marginTop: '85px',
  },
  /**/
  sheet: {
    touchAction: 'none',
  },
});

const recordStates = {
  PERSISTED: 'save',
  SENT: 'check',
  ERROR: 'error',
  WARNING: 'warning',
  NOTE: 'note',
};
export default function TableExecution(props) {
  const {accumulatorTimeThreshold} = props;
  const {
    openCard,
    keyboardDetected,
    handleOpenDialogRecordDetails,
    initialized,
    stateRoute,
    records,
    availableHeight,
    rowHeight,
    setAvailableHeight
  } = useContext(TravelContext);
  const { t } = useTranslation('travelsPage');
  const classes = useStyles();
  const listRef = useRef(null);
  const [indexDragModified, setIndexDragModified] = useState();

  function createData(type, state, timeSent, timestamp, vel, doors, input, output, desvi, record) {
    return { type, state, timeSent, timestamp, vel, doors, input, output, desvi, record };
  }
  const rows = (records || []).map((data) => {
    if (data.type === 'RECORD') {
      const {
        state,
        timestamp,
        tripTempState: {
          frontDoorOpen,
          backDoorOpen,
          geolocationPosition = {},
          frontDoorInput = 0,
          frontDoorOutput = 0,
          backDoorInput = 0,
          backDoorOutput = 0,
        },
      } = data;

      return createData(
        data.type,
        recordStates[state],
        state === 'PERSISTED' ? null : data.stats.sentTimestamp - timestamp,
        timestamp,
        ((geolocationPosition || {}).coords || {}).speed || 0,
        `${frontDoorOpen ? 'DA' : 'DC'}${backDoorOpen ? 'TA' : 'TC'}`,
        `${frontDoorInput}/${backDoorInput}`,
        `${frontDoorOutput}/${backDoorOutput}`,
        ((geolocationPosition || {}).coords || {}).dop || 99.9,
        data
      );
    }
    return data;
  });
  useEffect(() => {
    setTimeout(() => {
      setIndexDragModified(undefined);
    }, 100);
  }, [openCard]);

  return (
    <div className="overflow-hidden relative">
      {openCard && (
        <div className="w-full">
          <Deck {...{ setIndexDragModified, accumulatorTimeThreshold }} />
        </div>
      )}

      <div
        className={`${
          openCard &&
          !keyboardDetected &&
          (indexDragModified > 0 ? true : indexDragModified === undefined)
            ? classes.marginTopTable
            : openCard &&
              keyboardDetected &&
              (indexDragModified > 0 ? true : indexDragModified === undefined)
            ? classes.marginTopTableTwo
            : 'mt-0'
        } w-full`}
      >
        <div size="small" className={`${classes.tableExecution}`}>
          <div>
            <div align="center">#</div>
            <div />

            <div>{t('TRAVEL_CONTENT_HOUR')}</div>
            <div>{t('TRAVEL_CONTENT_VEL')}</div>
            <div>{t('TRAVEL_CONTENT_DOORS')}</div>
            <div align="center">
              <Icon>keyboard_arrow_up</Icon>
            </div>
            <div align="center">
              <Icon>keyboard_arrow_down</Icon>
            </div>
            <div>{t('TRAVEL_CONTENT_DESV')}</div>
          </div>

          {((initialized && stateRoute === null) || (!initialized && stateRoute !== null)) && (
            <FixedSizeList
              height={availableHeight}
              width="100%"
              itemSize={rowHeight}
              itemCount={(rows || []).length}
              itemData={{
                itemsArray: rows || [],
                handleOpenDialogRecordDetails,
                style: classes,
                array: rows || [],
              }}
            >
              {Row}
            </FixedSizeList>
          )}
        </div>
      </div>
    </div>
  );
}
