import { useState, useEffect } from 'react';
import FusePageSimple from '@fuse/core/FusePageSimple';
import PropTypes from 'prop-types';
import { useTranslation } from 'react-i18next';
import { useSelector } from 'react-redux';
import { useSnackbar } from 'notistack';
import { styled } from '@mui/material/styles';
import { Hidden, Button, Tabs, Tab, Box } from '@mui/material';
import { makeStyles } from '@mui/styles';
import _ from '@lodash';
import WsRest from './tabs/WsRest';
import Travels from './tabs/Travels';
import NavbarToggleButton from '../../fuse-layouts/shared-components/NavbarToggleButton';

const Root = styled(FusePageSimple)({
  '& .FusePageSimple-header': {
    height: 'auto',
    minHeight: 'auto',
  },
  '& .FusePageSimple-toolbar': {},
  '& .FusePageSimple-content': {},
  '& .FusePageSimple-sidebarHeader': {},
  '& .FusePageSimple-sidebarContent': {},
});

const useStyles = makeStyles({});

const height = 160;
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box className="p-10">
          <div>{children}</div>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function SettingPage(props) {
  const { t } = useTranslation('settingPage');
  const configFuse = useSelector(({ fuse }) => fuse.settings.current.layout.config);
  const navbar = useSelector(({ fuse }) => fuse.navbar);
  const [tabValue, setTabValue] = useState(0);
  const { enqueueSnackbar } = useSnackbar();

  // WSREST
  const [endPoint, setEndPoint] = useState(
    'https://gtpc.metropol.gov.co/gtpc/servicesRest/archivos/procesarRecorrido'
  );
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');
  const [resultTestConnection, setResultTestConnection] = useState(0);
  const [numberOfAttemps, setNumberOfAttemps] = useState(3);
  const [waitTimeBetweenRetries, setWaitTimeBetweenRetries] = useState(150);
  const [timeout, seTimeout] = useState(5000);

  // TRAVEL
  const [countdown, setCountdown] = useState(3000);

  // TRAVEL: registros con ruta
  const [advanceVehicle, setAdvanceVehicle] = useState(240);
  const [elapse, setElapse] = useState(30);
  const [speed, setSpeed] = useState(0);
  const [useDoorStateAsTrigger, setUseDoorStateAsTrigger] = useState(true);

  // TRAVEL: registros sin ruta
  const [withoutAdvanceVehicle, setWithoutAdvanceVehicle] = useState(100);
  const [withoutElapse, setWithoutElapse] = useState(1800);
  const [withoutSpeed, setWithoutSpeed] = useState(0);
  const [withoutUseDoorStateAsTrigger, setWithoutUseDoorStateAsTrigger] = useState(true);

  const dataTransmissionConfigLocalStorage = localStorage.getItem('transmissionConfig');
  const dataRecordGenerationRules = localStorage.getItem('recordGenerationRules');

  function handleChangeTab(event, tabValueToSet) {
    setTabValue(tabValueToSet);
  }

  function validateUrl(url) {
    return typeof url === 'string' &&
      url.length >= 0 &&
      /^(?:(?:(?:https?|ftp):)?\/\/)(?:\S+(?::\S*)?@)?(?:(?!(?:10|127)(?:\.\d{1,3}){3})(?!(?:169\.254|192\.168)(?:\.\d{1,3}){2})(?!172\.(?:1[6-9]|2\d|3[0-1])(?:\.\d{1,3}){2})(?:[1-9]\d?|1\d\d|2[01]\d|22[0-3])(?:\.(?:1?\d{1,2}|2[0-4]\d|25[0-5])){2}(?:\.(?:[1-9]\d?|1\d\d|2[0-4]\d|25[0-4]))|(?:(?:[a-z\u00a1-\uffff0-9]-*)*[a-z\u00a1-\uffff0-9]+)(?:\.(?:[a-z\u00a1-\uffff0-9]-*)*[a-z\u00a1-\uffff0-9]+)*(?:\.(?:[a-z\u00a1-\uffff]{2,})))(?::\d{2,5})?(?:[/?#]\S*)?$/i.test(
        url
      )
      ? null
      : t('INVALID_URL');
  }

  function validateString(value, minLen, maxLen) {
    return typeof value === 'string' && value.length >= minLen && value.length <= maxLen
      ? null
      : `Debe contener entre ${minLen} y ${maxLen} caracteres`;
  }

  function validateNumber(num, minVal, maxVal) {
    return typeof num === 'number' && num >= minVal && num <= maxVal
      ? null
      : `Debe tener un valor entre ${minVal} y ${maxVal}`;
  }

  const valueFocusCleaner = (value) => {
    return value.trim();
  };

  const buildTransmissionConfig = () => ({
    user,
    password,
    url: endPoint,
    timeout,
    maxRetires: numberOfAttemps,
    delayBetweenRetries: waitTimeBetweenRetries,
  });

  const buildRecordGenerationRules = () => ({
    accumulatorTimeThreshold: countdown,
    withRoute: {
      distanceThreshold: advanceVehicle,
      timeThreshold: elapse,
      speedThreshold: speed,
      useDoorStateAsTrigger,
    },
    withoutRoute: {
      distanceThreshold: withoutAdvanceVehicle,
      timeThreshold: withoutElapse,
      speedThreshold: withoutSpeed,
      useDoorStateAsTrigger: withoutUseDoorStateAsTrigger,
    },
  });

  function canBeSubmitted() {
    const canSubmit =
      !validateString(password, 0, 200) &&
      !validateString(user, 0, 200) &&
      !validateUrl(endPoint) &&
      !validateNumber(timeout, 0, 600000) &&
      !validateNumber(numberOfAttemps, 0, 100) &&
      !validateNumber(waitTimeBetweenRetries, 0, 60000) &&
      !validateNumber(advanceVehicle, 0, 10000) &&
      !validateNumber(elapse, 0, 86400000) >= 0 &&
      !validateNumber(speed, 0, 500) &&
      !validateNumber(withoutAdvanceVehicle, 0, 10000) &&
      !validateNumber(withoutSpeed, 0, 500) &&
      !validateNumber(countdown, 0, 60000) &&
      !validateNumber(withoutElapse, 0, 86400000);

    const isTransmisionConfigDifferent = !_.isEqual(
      buildTransmissionConfig(),
      JSON.parse(localStorage.getItem('transmissionConfig') || '{}')
    );
    const isRecordGenerationRulesDifferent = !_.isEqual(
      buildRecordGenerationRules(),
      JSON.parse(localStorage.getItem('recordGenerationRules') || '{}')
    );
    return canSubmit && (isTransmisionConfigDifferent || isRecordGenerationRulesDifferent);
  }

  function handleSave() {
    localStorage.setItem('transmissionConfig', JSON.stringify(buildTransmissionConfig()));
    localStorage.setItem('recordGenerationRules', JSON.stringify(buildRecordGenerationRules()));
    enqueueSnackbar(t('Se guardo la configuraciÃ³n'), { variant: 'success' });
  }

  useEffect(() => {
    console.log('dataRecordGenerationRules', JSON.parse(dataRecordGenerationRules));
    if (dataTransmissionConfigLocalStorage) {
      const data = JSON.parse(dataTransmissionConfigLocalStorage);
      setEndPoint(data.url);
      setNumberOfAttemps(data.maxRetires);
      setUser(data.user);
      setPassword(data.password);
      setWaitTimeBetweenRetries(data.delayBetweenRetries);
      seTimeout(data.timeout);
    }
    if (dataRecordGenerationRules) {
      const data = JSON.parse(dataRecordGenerationRules);
      setCountdown(data.accumulatorTimeThreshold);
      setAdvanceVehicle(data.withRoute.distanceThreshold);
      setElapse(data.withRoute.timeThreshold);
      setSpeed(data.withRoute.speedThreshold);
      setUseDoorStateAsTrigger(data.withRoute.useDoorStateAsTrigger);

      setWithoutAdvanceVehicle(data.withoutRoute.distanceThreshold);
      setWithoutElapse(data.withoutRoute.timeThreshold);
      setWithoutSpeed(data.withoutRoute.speedThreshold);
      setWithoutUseDoorStateAsTrigger(data.withoutRoute.useDoorStateAsTrigger);
    }
  }, [dataTransmissionConfigLocalStorage, dataRecordGenerationRules]);

  const childrenProps = {
    endPoint,
    setEndPoint,
    user,
    setUser,
    password,
    setPassword,
    resultTestConnection,
    setResultTestConnection,
    numberOfAttemps,
    setNumberOfAttemps,
    waitTimeBetweenRetries,
    setWaitTimeBetweenRetries,
    timeout,
    seTimeout,
    valueFocusCleaner,
    countdown,
    setCountdown,
    advanceVehicle,
    setAdvanceVehicle,
    elapse,
    setElapse,
    speed,
    setSpeed,
    withoutAdvanceVehicle,
    setWithoutAdvanceVehicle,
    withoutElapse,
    setWithoutElapse,
    withoutSpeed,
    setWithoutSpeed,
    useDoorStateAsTrigger,
    setUseDoorStateAsTrigger,
    withoutUseDoorStateAsTrigger,
    setWithoutUseDoorStateAsTrigger,
    validateUrl,
    validateString,
    validateNumber,
  };

  return (
    <Root
      header={
        <div className="w-full flex flex-wrap place-content-start">
          <div className="w-full py-10 px-16 flex items-start justify-between">
            <div>
              {configFuse.navbar.display && configFuse.navbar.position === 'left' && (
                <>
                  <Hidden lgDown>
                    {(configFuse.navbar.style === 'style-3' ||
                      configFuse.navbar.style === 'style-3-dense') && (
                      <NavbarToggleButton className="w-40 h-40 p-0 mx-0" />
                    )}

                    {configFuse.navbar.style === 'style-1' && !navbar.open && (
                      <NavbarToggleButton className="w-40 h-40 p-0 mx-0" />
                    )}
                  </Hidden>

                  <Hidden lgUp>
                    <NavbarToggleButton className="w-40 h-40 p-0 mx-0 sm:mx-8" />
                  </Hidden>
                </>
              )}
            </div>

            <div>
              <Button
                variant="contained"
                color="success"
                disabled={!canBeSubmitted()}
                onClick={handleSave}
              >
                {t('SAVE')}
              </Button>
            </div>
          </div>
        </div>
      }
      content={
        <Box sx={{ width: '100%' }}>
          <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
            <Tabs
              variant="scrollable"
              value={tabValue}
              onChange={handleChangeTab}
              aria-label="basic tabs example"
            >
              <Tab label="WS REST" id={`simple-tab-${0}`} aria-controls={`simple-tabpanel-${0}`} />
              <Tab label="Viaje" id={`simple-tab-${1}`} aria-controls={`simple-tabpanel-${1}`} />
            </Tabs>
          </Box>
          <TabPanel value={tabValue} index={0}>
            <WsRest {...childrenProps} />
          </TabPanel>
          <TabPanel value={tabValue} index={1}>
            <Travels {...childrenProps} />
          </TabPanel>
        </Box>
      }
    />
  );
}

export default SettingPage;
