import PropTypes from 'prop-types';
import styled from 'styled-components';
import Tooltip from '@mui/material/Tooltip';

const Wrapper = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  width: 18px;
  height: 18px;
  background-color: #00000077;
  border: 2px solid #039be5;
  border-radius: 100%;
  user-select: none;
  transform: translate(-50%, -50%);
  cursor: ${(props) => (props.onClick ? 'pointer' : 'default')};
  &:hover {
    z-index: 1;
  }
`;

const getCustomStyles = (hasError, type) => {
  if (hasError)
    return {
      width: `25px`,
      height: `25px`,
      backgroundColor: '#A7193050',
      border: type === 'APP' ? '2px solid #7045d7' : '2px solid #2e7fe2',
    };
  return {
    width: `25px`,
    height: `25px`,
    backgroundColor: type === 'APP' ? '#7045d759' : '#2e7fe27a',
    border: type === 'APP' ? '2px solid #7045d7' : '2px solid #2e7fe2',
  };
};

const Marker = ({ hasError, text, lat, lng, type, onClick }) => (
  <Tooltip title={text || ''} placement="top">
    <Wrapper alt={text} lat={lat} lng={lng} style={getCustomStyles(hasError, type)} />
  </Tooltip>
);

Marker.defaultProps = {
  onClick: null,
};

Marker.propTypes = {
  onClick: PropTypes.func,
  text: PropTypes.string.isRequired,
};

export default Marker;
