import { Typography, Button, TextField, Icon, InputAdornment } from '@mui/material';
import { useTranslation } from 'react-i18next';
import { makeStyles } from '@mui/styles';
import { useSnackbar } from 'notistack';
import ProxyService from '../../../services/proxy-service';

const useStyles = makeStyles({
  titles: {
    paddingLeft: '8px',
  },
  inputNumber: {
    '& input[type=number]::-webkit-inner-spin-button, input[type=number]::-webkit-outer-spin-button':
      {
        '-webkit-appearance': 'none',
        margin: '0',
      },

    '& input[type=number]': {
      '-moz-appearance': 'textfield',
    },
  },
});

export default function WsRest(props) {
  const {
    endPoint,
    setEndPoint,
    user,
    setUser,
    password,
    setPassword,
    resultTestConnection,
    setResultTestConnection,
    numberOfAttemps,
    setNumberOfAttemps,
    waitTimeBetweenRetries,
    setWaitTimeBetweenRetries,
    timeout,
    seTimeout,
    validateUrl,
    validateString,
    validateNumber,
    valueFocusCleaner,
  } = props;
  const { t } = useTranslation('settingPage');
  const classes = useStyles();
  const { enqueueSnackbar } = useSnackbar();

  function handleEndPointChange(event) {
    setEndPoint(event.target.value);
  }

  function handleUserChange(event) {
    setUser(event.target.value);
  }

  function handlePasswordChange(event) {
    setPassword(event.target.value);
  }

  function handleNumberOfAttemps(event) {
    setNumberOfAttemps(parseInt(event.target.value, 10));
  }

  function handleWaitTimeBetweenRetries(event) {
    setWaitTimeBetweenRetries(parseInt(event.target.value, 10));
  }

  function handleTimeout(event) {
    seTimeout(parseInt(event.target.value, 10));
  }

  const handleTestConnection = () => {
    ProxyService.test(endPoint, user, password, timeout)
      .then(({ data }) => {
        const success =
          data &&
          data.statusDTO &&
          data.statusDTO.descripcion === 'Ocurri贸 un error cargando el archivo.';
        console.log({ success });
        if (success) {
          enqueueSnackbar('Conexi贸n Exitosa', { variant: 'success' });
        } else {
          enqueueSnackbar(
            `Conexi贸n Errada: codigo:${data.statusDTO.codigo}, descripcion:${data.statusDTO.descripcion}`,
            { variant: 'error' }
          );
        }
      })
      .catch((error) => {
        console.log({ success: false, error });
        console.error(error);
        enqueueSnackbar(`Conexi贸n Errada: ${error.message}`, { variant: 'error' });
      });
  };

  return (
    <div>
      <div>
        <Typography>{t('PARAMETER_TEXT')}</Typography>
      </div>
      <br />
      <div className="w-full flex flex-wrap">
        <div className="w-full">
          <Typography>{t('ENDPOINT')}: </Typography>
        </div>
        <TextField
          className="mt-8 mb-16 flex w-full sm:w-full sm:pl-3"
          id="endPoint"
          error={validateUrl(endPoint) !== null}
          helperText={validateUrl(endPoint)}
          value={endPoint}
          onChange={handleEndPointChange}
        />

        <div className="w-full">
          <Typography>{t('USER')}: </Typography>
        </div>
        <TextField
          className="mt-8 mb-16 flex w-full sm:w-full sm:pl-3"
          id="user"
          error={validateString(user, 0, 200) !== null}
          helperText={validateString(user, 0, 200)}
          value={user}
          onChange={handleUserChange}
          onBlur={(evt) => {
            evt.target.value = valueFocusCleaner(evt.target.value);
            handleUserChange(evt);
          }}
        />

        <div className="w-full">
          <Typography>{t('PASSWORD')}: </Typography>
        </div>
        <TextField
          className="mt-8 mb-16 flex w-full sm:w-full sm:pl-3"
          id="password"
          error={validateString(password, 0, 200) !== null}
          helperText={validateString(password, 0, 200)}
          value={password}
          type="password"
          onChange={handlePasswordChange}
          onBlur={(evt) => {
            evt.target.value = valueFocusCleaner(evt.target.value);
            handlePasswordChange(evt);
          }}
        />

        <div className="w-full">
          <Typography className="leading-none mb-10">
            <b>{t('REST_SETTINGS')}:</b>
          </Typography>
        </div>

        <div
          className={`w-full sm:w-1/2 flex pr-0 sm:pr-10 items-center ${classes.inputNumber} justify-between`}
        >
          <Typography className="w-full">{t('TIMEOUT')}</Typography>

          <TextField
            id="timeout"
            error={validateNumber(timeout, 0, 600000) !== null}
            helperText={validateNumber(timeout, 0, 600000)}
            sx={{ m: 1, width: '30ch' }}
            type="number"
            value={timeout}
            onChange={handleTimeout}
            InputProps={{
              endAdornment: <InputAdornment position="end">milisegundos</InputAdornment>,
            }}
          />
        </div>

        <div
          className={`w-full sm:w-1/2 flex pl-0 sm:pl-10 items-center ${classes.inputNumber} justify-between`}
        >
          <Typography className="w-full">{t('NUMBER_OF_ATTEMPS')}</Typography>

          <TextField
            id="numberOfAttemps"
            error={validateNumber(numberOfAttemps, 0, 100) !== null}
            helperText={validateNumber(numberOfAttemps, 0, 100)}
            sx={{ m: 1, width: '30ch' }}
            type="number"
            value={numberOfAttemps}
            onChange={handleNumberOfAttemps}
            InputProps={{
              endAdornment: <InputAdornment position="end">intentos</InputAdornment>,
            }}
          />
        </div>

        <div
          className={`w-full sm:w-1/2 flex pr-0 sm:pr-10 items-center ${classes.inputNumber} justify-between`}
        >
          <Typography className="w-full">{t('WAIT_TIME_BETWEEN_RETRIES')}</Typography>

          <TextField
            id="waitTimeBetweenRetries"
            error={validateNumber(waitTimeBetweenRetries, 0, 60000) !== null}
            helperText={validateNumber(waitTimeBetweenRetries, 0, 60000)}
            sx={{ m: 1, width: '30ch' }}
            type="number"
            value={waitTimeBetweenRetries}
            onChange={handleWaitTimeBetweenRetries}
            InputProps={{
              endAdornment: <InputAdornment position="end">milisegundos</InputAdornment>,
            }}
          />
        </div>

        <Button
          className="w-full"
          variant="contained"
          disabled={
            validateString(password, 0, 200) ||
            validateString(user, 0, 200) ||
            validateUrl(endPoint) ||
            validateNumber(timeout, 0, 600000)
          }
          onClick={handleTestConnection}
        >
          {t('TEST_CONNECTION')}
        </Button>
      </div>
      <br />
      {resultTestConnection === 200 ? (
        <div className="flex items-center">
          <Icon fontSize="large">check_circle_sharp_icon</Icon>{' '}
          <Typography>{t('CONNECTION_SUCCES')}</Typography>
        </div>
      ) : resultTestConnection !== 200 && resultTestConnection !== 0 ? (
        <div className="flex items-center">
          <Icon fontSize="large">cancel</Icon> <Typography>{t('CONNECTION_FAILED')}</Typography>
        </div>
      ) : null}
    </div>
  );
}
